package screens;

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.net.URL;
import java.util.ResourceBundle;
import javafx.fxml.FXML;
import javafx.fxml.Initializable;
import javafx.scene.image.Image;
import javafx.scene.image.ImageView;
import javafx.scene.layout.GridPane;
import security.Authenticator;
import sun.misc.BASE64Decoder;

public class FXMLHeaderInfosController implements Initializable {
    
    @FXML
    private ImageView avatarHolder;
    
    @FXML
    private GridPane userInfosMenu;    
    
    /**
     * Initializes the controller class.
     */
    @Override
    public void initialize(URL url, ResourceBundle rb) {       
        // Visible IF user is authenticated
        userInfosMenu.visibleProperty().bind(Authenticator.isAuth);
        
        if(Authenticator.isAuth.getValue()) {
            try {
                // @TODO : create a getAvatar() method on User class
//                if(Authenticator.user != null && Authenticator.user.getAvatar()) {
                if(Authenticator.user != null) {
                    ByteArrayInputStream imageStream;

                    String base64Img = "";
                    BASE64Decoder decoder = new BASE64Decoder();
                    imageStream = new ByteArrayInputStream(decoder.decodeBuffer(base64Img));
                    Image avatar = new Image(imageStream);

                    avatarHolder.setImage(avatar);    

                    imageStream.close();
                } else {
                    avatarHolder.setImage(new Image("/resources/icon_avatar_red.png"));
                }
            } catch(IOException ex) {
                System.err.println(ex);
            }            
        }
    }    
}